import unittest

from lightController import lightController

class TestLightController(unittest.TestCase):

    def testPacketEncoder(self):
        self.maxDiff = None
        payload = "00000000010101010101010111111111111111111111111111111111101011000000110100000000000000000000000000000000"
        self.assertEqual(lightController.encodePacket(1024,1,0,0,2,"d073d5001337",0,1,1,102,payload), "")

if __name__ == '__main__':
    unittest.main()